import "./result.css"

 import ResultItem from "./resultitem";



const Result = (props) => {



    const [allResults,setAllResults] =React.useState([]);

    setAllResults(props.getresult)



  return (
    <table className="result">
      <thead>
        <tr>
          <th>Year</th>
          <th>Total Savings</th>
          <th>Interest (Year)</th>
          <th>Total Interest</th>
          <th>Invested Capital</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>YEAR NUMBER</td>
          <td>TOTAL SAVINGS END OF YEAR</td>
          <td>INTEREST GAINED IN YEAR</td>
          <td>TOTAL INTEREST GAINED</td>
          <td>TOTAL INVESTED CAPITAL</td>
        </tr>
        {
          props.getresult.map((element) =>{
            <ResultItem year={element.year} savingsEndofYear={element.savingsEndofYear} yearlyInterest={element.yearlyInterest} yearlyContribution={element.yearlyContribution}></ResultItem>
          })
        }


      </tbody>
    </table>

  );
}

export default Result;
